    <script type="text/javascript">
        var recheckIntervalInSeconds = 60;
        var reloadWhenReadyInSeconds = 30;
        var intervalHandle = setInterval(checkForUptime, recheckIntervalInSeconds*1000);

        async function checkForUptime() {
            const response = await fetch("/test.html");

            if (parseInt(response.status) === 200) {
                clearInterval(intervalHandle);
                console.log("We are back up and will reload site");
                //refresh window because we believe we are back in business
                setTimeout(() => {
                    window.location.reload(true);
                }, reloadWhenReadyInSeconds * 1000);
            } else {
                //check again in {recheckIntervalInSeconds} seconds
                console.log("Still down");
            }
        }
    </script>
